import React from "react";
import "./login.css";
import { useNavigate } from "react-router-dom"; 

export const Login = () => {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    console.log("Logging in...");
    navigate("/2fa"); // Navigate to 2FA page after login
  };

  const handleSignupNavigation = () => {
    navigate("/auth/signup"); // Navigate to Signup page
  };

  const handleForgotPasswordNavigation = () => {
    navigate("/forgotPassword"); // Navigate to Forgot Password page
  };

  return (
    <div className="login-left">
      <div className="login-form">
        <h1>Login</h1>
        <form>
          <div className="email-input">
            <input type="email" placeholder="Enter email" />
          </div>
          <div className="password-input">
            <input type="password" placeholder="Enter password" />
          </div>
          <button type="button" className="forgot-password" onClick={handleForgotPasswordNavigation}>
            Forgot Password?
          </button>
          <button type="submit" className="login-button" onClick={handleLogin}>
            Login
          </button>
        </form>
        <div className="signup">
          Don't have an account? 
          <button type="button" className="signup-link" onClick={handleSignupNavigation}>
            Sign up here
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
