import React, { useState } from "react";
import "./manageRoles.css";

const ManageRoles = ({ selectedTeam, onBack }) => {
  const [roles, setRoles] = useState([]);
  const [showAddRoleModal, setShowAddRoleModal] = useState(false);
  const [newRoleName, setNewRoleName] = useState("");

  const addRole = () => {
    if (newRoleName.trim() !== "") {
      setRoles([...roles, { id: roles.length + 1, name: newRoleName }]);
      setShowAddRoleModal(false);
    }
  };

  return (
    <div className="roles-container">
      <div className="section-banner">
        <button className="back-button" onClick={onBack}>‚Üê</button>
        <h2>{selectedTeam} - Manage Roles</h2>
      </div>

      <div className="roles-list">
        {roles.map((role) => (
          <div key={role.id} className="role-item">
            <div className="role-header">{role.name}</div>
          </div>
        ))}
      </div>

      <button className="add-role-button" onClick={() => setShowAddRoleModal(true)}>Add Role</button>
    </div>
  );
};

export default ManageRoles;
