import React from "react";
import { useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./myMembers.css";

const MyMembers = ({ selectedTeam, employees = [], onBack }) => {
  const navigate = useNavigate();

  const renderMemberRow = (member) => (
    <div className="member-row" key={member.id}>
      <img
        src={member.photo}
        alt={`${member.firstName} ${member.lastName}`}
        className="member-photo"
      />
      <div className="member-name">
        {member.firstName} {member.lastName}
      </div>
      <div className="member-email">{member.email}</div>
      <div className="member-phone">{member.phone}</div>
      <div className="member-roles">{member.roles}</div>
    </div>
  );

  return (
    <div className="members-view">
      {/* Back and Profile Buttons Above Banner */}
      <div className="banner-controls">
        <button className="back-button" onClick={onBack}>‚Üê</button>
        <button className="profile-button" onClick={() => navigate("/settings")}>
          <FaUserCircle />
        </button>
      </div>

      {/* Main Banner */}
      <div className="section-banner">
        <h2>{selectedTeam} - My Members</h2>
      </div>

      <div className="members-roster">
        {employees.length > 0 ? (
          employees.map((employee) => renderMemberRow(employee))
        ) : (
          <p className="empty-message">No members found.</p>
        )}
      </div>
    </div>
  );
};

export default MyMembers;
