import React, { useState } from "react";
import ManageRoles from "../components/manageRoles"; // Already moved to components
import MyMembers from "../components/myMembers"; // Newly created component
import PasswordAndFileManagement from "../components/passwordAndFileManage"; // Password and File Management component
import "./team_pw_manager.css";

const TeamPwManager = () => {
  const [teams, setTeams] = useState([
    "My Company 1",
    "My Company 2",
    "My Company 3",
    "My Company 4",
  ]);
  const [memberships, setMemberships] = useState([
    "My Membership 1",
    "My Membership 2",
    "My Membership 3",
    "My Membership 4",
  ]);
  const [selectedTeam, setSelectedTeam] = useState(teams[0]);
  // Track which view is active: "members", "roles", "password", or null (default home)
  const [selectedOption, setSelectedOption] = useState(null);

  // States for expanded lists
  const [expandedTeams, setExpandedTeams] = useState(false);
  const [expandedMemberships, setExpandedMemberships] = useState(false);

  // States for inline team creation
  const [showTeamInput, setShowTeamInput] = useState(false);
  const [newTeamName, setNewTeamName] = useState("");

  // States for inline membership creation
  const [showMembershipInput, setShowMembershipInput] = useState(false);
  const [newMembershipName, setNewMembershipName] = useState("");

  // States for inline editing teams
  const [editingTeamIndex, setEditingTeamIndex] = useState(null);
  const [editingTeamName, setEditingTeamName] = useState("");

  // States for inline editing memberships
  const [editingMembershipIndex, setEditingMembershipIndex] = useState(null);
  const [editingMembershipName, setEditingMembershipName] = useState("");

  // "employee" data for "My Members"
  const [employees] = useState([
    {
      id: 1,
      firstName: "John",
      lastName: "Doe",
      email: "john.doe@example.com",
      phone: "123-456-7890",
      roles: "Manager",
      photo: "https://via.placeholder.com/50",
    },
    {
      id: 2,
      firstName: "Jane",
      lastName: "Smith",
      email: "jane.smith@example.com",
      phone: "987-654-3210",
      roles: "Developer",
      photo: "https://via.placeholder.com/50",
    },
  ]);

  // Handle inline team input submission (for adding a new team)
  const handleTeamInputKeyDown = (e) => {
    if (e.key === "Enter") {
      if (newTeamName.trim() !== "") {
        const trimmedName = newTeamName.trim();
        setTeams([...teams, trimmedName]);
        setNewTeamName("");
        setShowTeamInput(false);
      } else {
        alert("Team name cannot be blank.");
      }
    } else if (e.key === "Escape") {
      setNewTeamName("");
      setShowTeamInput(false);
    }
  };

  // Handle inline membership input submission (for adding a new membership)
  const handleMembershipInputKeyDown = (e) => {
    if (e.key === "Enter") {
      if (newMembershipName.trim() !== "") {
        const trimmedName = newMembershipName.trim();
        setMemberships([...memberships, trimmedName]);
        setNewMembershipName("");
        setShowMembershipInput(false);
      } else {
        alert("Membership name cannot be blank.");
      }
    } else if (e.key === "Escape") {
      setNewMembershipName("");
      setShowMembershipInput(false);
    }
  };

  // Handle inline editing for teams
  const handleEditingTeamKeyDown = (e, index) => {
    if (e.key === "Enter") {
      if (editingTeamName.trim() !== "") {
        const updatedTeams = [...teams];
        updatedTeams[index] = editingTeamName.trim();
        setTeams(updatedTeams);
        setEditingTeamIndex(null);
        setEditingTeamName("");
      } else {
        alert("Team name cannot be blank.");
      }
    } else if (e.key === "Escape") {
      setEditingTeamIndex(null);
      setEditingTeamName("");
    }
  };

  // Handle inline editing for memberships
  const handleEditingMembershipKeyDown = (e, index) => {
    if (e.key === "Enter") {
      if (editingMembershipName.trim() !== "") {
        const updatedMemberships = [...memberships];
        updatedMemberships[index] = editingMembershipName.trim();
        setMemberships(updatedMemberships);
        setEditingMembershipIndex(null);
        setEditingMembershipName("");
      } else {
        alert("Membership name cannot be blank.");
      }
    } else if (e.key === "Escape") {
      setEditingMembershipIndex(null);
      setEditingMembershipName("");
    }
  };

  // Delete a team with confirmation
  const deleteTeam = (team) => {
    if (window.confirm(`Are you sure you want to delete "${team}"?`)) {
      setTeams(teams.filter((t) => t !== team));
      if (selectedTeam === team) {
        setSelectedTeam(teams.length > 1 ? teams[0] : null);
      }
    }
  };

  // Delete a membership with confirmation
  const deleteMembership = (membership) => {
    if (window.confirm(`Are you sure you want to delete "${membership}"?`)) {
      setMemberships(memberships.filter((m) => m !== membership));
    }
  };

  // Determine items to display based on expanded state
  const previewCount = 3;
  const displayedTeams = expandedTeams ? teams : teams.slice(0, previewCount);
  const displayedMemberships = expandedMemberships ? memberships : memberships.slice(0, previewCount);

  return (
    <div className="teams-container">
      {/* Left Sidebar */}
      <div className="sidebar">
        <div className="sidebar-heading">
          <div className="sidebar-heading-top">Teams</div>
          <div className="sidebar-heading-bottom">Passwords & Files</div>
        </div>

        {/* Teams Section */}
        <div className="sidebar-section">
          <div className="section-header">
            <h2>My Teams</h2>
            <button className="section-add-button" onClick={() => setShowTeamInput(true)}>
              +
            </button>
          </div>
          <div className={`teams-list-container ${expandedTeams ? "scrollable" : ""}`}>
            {displayedTeams.map((team, index) => (
              <div key={index} className="team-item">
                {editingTeamIndex === index ? (
                  <input
                    type="text"
                    className="team-input"
                    value={editingTeamName}
                    onChange={(e) => setEditingTeamName(e.target.value)}
                    onKeyDown={(e) => handleEditingTeamKeyDown(e, index)}
                    autoFocus
                    onBlur={() => {
                      if (editingTeamName.trim() !== "") {
                        const updatedTeams = [...teams];
                        updatedTeams[index] = editingTeamName.trim();
                        setTeams(updatedTeams);
                      }
                      setEditingTeamIndex(null);
                      setEditingTeamName("");
                    }}
                  />
                ) : (
                  <button
                    className={`team-button ${selectedTeam === team ? "active" : ""}`}
                    onClick={() => {
                      setSelectedTeam(team);
                      setSelectedOption(null);
                    }}
                    onDoubleClick={() => {
                      setEditingTeamIndex(index);
                      setEditingTeamName(team);
                    }}
                  >
                    <span className="team-name">{team}</span>
                  </button>
                )}
              </div>
            ))}
            {showTeamInput && (
              <div className="team-item">
                <input
                  type="text"
                  className="team-input"
                  value={newTeamName}
                  onChange={(e) => setNewTeamName(e.target.value)}
                  onKeyDown={handleTeamInputKeyDown}
                  autoFocus
                  placeholder="Enter new team name"
                />
              </div>
            )}
          </div>
          {teams.length > previewCount && (
            <button
              className="see-toggle-button"
              onClick={() => setExpandedTeams(!expandedTeams)}
            >
              {expandedTeams ? "See Less" : "See More"}
            </button>
          )}
        </div>

        <hr className="divider" />

        {/* Memberships Section */}
        <div className="sidebar-section">
          <div className="section-header">
            <h2>My Memberships</h2>
            <button className="section-add-button" onClick={() => setShowMembershipInput(true)}>
              +
            </button>
          </div>
          <div className={`memberships-list-container ${expandedMemberships ? "scrollable" : ""}`}>
            {displayedMemberships.map((membership, index) => (
              <div key={index} className="team-item">
                {editingMembershipIndex === index ? (
                  <input
                    type="text"
                    className="membership-input"
                    value={editingMembershipName}
                    onChange={(e) => setEditingMembershipName(e.target.value)}
                    onKeyDown={(e) => handleEditingMembershipKeyDown(e, index)}
                    autoFocus
                    onBlur={() => {
                      if (editingMembershipName.trim() !== "") {
                        const updatedMemberships = [...memberships];
                        updatedMemberships[index] = editingMembershipName.trim();
                        setMemberships(updatedMemberships);
                      }
                      setEditingMembershipIndex(null);
                      setEditingMembershipName("");
                    }}
                  />
                ) : (
                  <button
                    className="membership-button"
                    onDoubleClick={() => {
                      setEditingMembershipIndex(index);
                      setEditingMembershipName(membership);
                    }}
                  >
                    <span className="team-name">{membership}</span>
                  </button>
                )}
              </div>
            ))}
            {showMembershipInput && (
              <div className="team-item">
                <input
                  type="text"
                  className="membership-input"
                  value={newMembershipName}
                  onChange={(e) => setNewMembershipName(e.target.value)}
                  onKeyDown={handleMembershipInputKeyDown}
                  autoFocus
                  placeholder="Enter new membership name"
                />
              </div>
            )}
          </div>
          {memberships.length > previewCount && (
            <button
              className="see-toggle-button"
              onClick={() => setExpandedMemberships(!expandedMemberships)}
            >
              {expandedMemberships ? "See Less" : "See More"}
            </button>
          )}
        </div>
      </div>

      {/* Main Content Area */}
      <div className="main-content">
        {selectedOption === "members" ? (
          <MyMembers employees={employees} onBack={() => setSelectedOption(null)} />
        ) : selectedOption === "roles" ? (
          <div className="roles-view">
            <div className="back-button-wrapper">
              <button className="back-button" onClick={() => setSelectedOption(null)}>
                Back
              </button>
            </div>
            <ManageRoles />
          </div>
        ) : selectedOption === "password" ? (
          <div className="password-file-view">
            <div className="back-button-wrapper">
              <button className="back-button" onClick={() => setSelectedOption(null)}>
                Back
              </button>
            </div>
            <PasswordAndFileManagement />
          </div>
        ) : (
          <div>
            <h1>{selectedTeam}</h1>
            <div className="options-container">
              <button className="option-button" onClick={() => setSelectedOption("password")}>
                Password and File Management
              </button>
              <button className="option-button" onClick={() => setSelectedOption("members")}>
                My Members
              </button>
              <button className="option-button" onClick={() => setSelectedOption("roles")}>
                Manage Roles
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TeamPwManager;
