import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import reportWebVitals from './reportWebVitals';
import {
  createBrowserRouter,
  RouterProvider,
  Route,
} from 'react-router-dom';
import AuthLayout from './pages/authLayout'; // Shared layout for Login/Signup
import Login from './pages/login';
import Signup from './pages/signup';
import ForgotPassword from './pages/forgotPassword';
import TwoFA from './pages/2fa';
import PersonalPwManager from './pages/personal_pw_manager';
import App from './App';

const router = createBrowserRouter([
  {
    path: "/",
    element: <App />, // Main App Page
  },
  {
    path: "/auth", // Group authentication-related pages under `/auth`
    element: <AuthLayout />, // Shared layout with persistent right-side UI
    children: [
      { path: "login", element: <Login /> },
      { path: "signup", element: <Signup /> },
    ],
  },
  {
    path: "/forgotPassword",
    element: <ForgotPassword />,
  },
  {
    path: "/2fa",
    element: <TwoFA />,
  },
  {
    path: "/personal-pw-manager",
    element: <PersonalPwManager />,
  },
]);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <RouterProvider router={router} />
  </React.StrictMode>
);

reportWebVitals();
